import React, { Component } from "react";
import SearchResults from "./components/search_results";
import DatasetPage from "./components/dataset_page";
import StaticPage from "./components/static_page";
import HistoryList from "./components/history_list";
import HistoryDetail from "./components/history_detail";
import Alertdialog from './components/dialogbox';
import Loadingicon from "./components/loading_icon";

import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from "./components/header";
import Footer from "./components/footer";


class App extends Component {

  state = {
    dialog:{
      status:false, 
      msg:""
    }
  };

  componentDidMount() {
    var reqObj = {};
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(reqObj)
    };
    const svcUrl = LocalConfig.apiHash.dataset_init;

    fetch(svcUrl, requestOptions)
      .then((res) => res.json())
      .then(
        (result) => {
          var tmpState = this.state;
          tmpState.response = result;
          tmpState.isLoaded = true;
          if (tmpState.response.status === 0){
            tmpState.dialog.status = true;
            tmpState.dialog.msg = tmpState.response.error;
          }
          this.setState(tmpState);
          //console.log("Request:",svcUrl);
          console.log("Ajax response:", result);
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }


  render() {
    var app_ver = process.env.REACT_APP_APP_VERSION;
    var data_ver = "x.x";
    return (
      <div>
      <Header onSearch={this.handleSearch} onKeyPress={this.handleKeyPress}/>
      <div className="versioncn">APP v-{app_ver} &nbsp; |&nbsp; Data v-{data_ver}</div>
      <Router>
        <Switch>
          <Route
            path="/history_list"
            render={(props) => (
              <HistoryList
              pageId={"History List"}
              />
            )}
          />
          <Route
            path="/:bcoId/history"
            render={(props) => (
              <HistoryDetail
              bcoId={props.match.params.bcoId}
              />
            )}
          />
          <Route
            path="/static/:pageId"
            render={(props) => (
              <StaticPage
                pageId={props.match.params.pageId}
              />
            )}
          />
          <Route
            path="/:bcoId"
            render={(props) => (
              <DatasetPage
                bcoId={props.match.params.bcoId}
              />
            )}
          />
          <Route
            exact
            path="/"
            render={(props) => (
              <SearchResults/>
            )}
          />
        </Switch>
      </Router>
      <Footer />
      </div>
    );


  }
}

export default App;
